				        ],\n										        \"rows\": [\n										          [\n										            {\n										              \"type\": \"text\",\n										              \"value\": \"E.8.1\"\n										            },\n										            {\n										              \"type\": \"textandcomments\",\n													  \"id\": \"CATPHAnnexEE8E81\",\n										              \"value\": \"<p><span class=\\\"panel-span-medium\\\">Access to the management console should be limited as much as possible with automation and scripting (via the AWS CLI) as the preferred deployment and management method in place of manual hands on management. (No access the management console over the Internet).</span></p>\"\n										            },\n										            {\n										              \"type\": \"radio\",\n										              \"paramname\": \"CATPHAnnexEE8E81Radio\",\n										              \"options\": [\n										                \"Yes\",\n										                \"No\",\n										                \"N/A\"\n										              ]\n										            },\n										            {\n										              \"type\": \"textarea\",\n										              \"paramname\": \"CATPHAnnexEE8E81Comments\",\n										              \"rows\": \"2\"\n										            }\n										          ],\n										          [\n										            {\n										              \"type\": \"text\",\n										              \"value\": \"E.8.2\"\n										            },\n										            {\n										              \"type\": \"textandcomments\",\n													  \"id\": \"CATPHAnnexEE8E82\",\n										              \"value\": \"<p><span class=\\\"panel-span-medium\\\">Access to the AWS management console must employ Two-factor authentication.</span></p>\"\n										            },\n										            {\n										              \"type\": \"radio\",\n										              \"paramname\": \"CATPHAnnexEE8E82Radio\",\n										              \"options\": [\n										                \"Yes\",\n									