SystemsDivData\": [\n    {\n	     \"panelheader\": {\n	     	\"value\": \"9. Networks Managed Systems (Not IT)\"\n	     }\n    },\n    {\n	     \"panelbody\": {\n	     	\"type\":\"paneltext\",\n	     	\"value\": \"<p> <span class=\\\"panel-span-medium\\\">Please answer the below questions to ensure the necessary security controls are implemented for the Networks managed infrastructure.<\\/span> <\\/p>\"	\n	     }\n	},\n	{\n     	\"breakline\": true\n    },\n	{\n	     \"panelbody\": {\n	     	\"type\":\"paneltable\",\n	     	\"value\": [\n	     		{\n	     			\"tables\": [\n				      {\n				        \"headers\": [\n				          \"#\",\n				          \"Network access control\",\n				          \"\",\n				          \"Notes\"\n				        ],\n				        \"width\": [\n				          \"1\",\n				          \"6\",\n				          \"3\",\n				          \"2\"\n				        ],\n				        \"rows\": [\n				          [\n				            {\n				              \"type\": \"text\",\n				              \"value\": \"9.1\"\n				            },\n				            {\n				              \"type\": \"textandcomments\",\n				              \"id\": \"NMS91\",\n				              \"value\": \"<p><span class=\\\"panel-span-medium\\\">Appropriate security controls shall be in place to segregate the Group</span></p>\"\n				            },\n				            {\n				              \"type\": \"radio\",\n				              \"paramname\": \"NMS91Radio\",\n				              \"options\": [\n				                \"Yes\",\n				                \"No\",\n				                \"N/A\"\n				              ]\n				            },\n				            {\n				              \"type\": \"textarea\",\n				              \"paramname\": \"NMS91Comments\",\n				              \"rows\": \"2\"\n				            }\n				          ],\n				          [\n				            {\n				              \"type\": \"text\",\n				              \"value\": \"9.2\"\n				            },\n				            {\n				              \"type\": \"textandcomments\",\n				              \"id\": \"NMS92\",\n				              \"value\": \"<p><span class=\\\"panel-span-medium\\\">Network access from external parties to any network of the Group shall be restricted to only allow access to perform the agreed services, and will require strong authentication or hard coded IDs to prevent source spoofing</span></p>\"\n				            },\n				            {\n				              \"type\": \"radio\",\n				              \"paramname\": \"NMS92Radio\",\n				              \"options\": [\n				                \"Yes\",\n				                \"No\",\n				                \"N/A\"\n				              ]\n				            },\n				            {\n				              \"type\": \"textarea\",\n				              \"paramname\": \"NMS92Comments\",\n				              \"rows\": \"2\"\n				            }\n				          ],\n				          [\n				            {\n				              \"type\": \"text\",\n				              \"value\": \"9.3\"\n				            },\n				            {\n				              \"type\": \"textandcomments\",\n				              \"id\": \"NMS93\",\n				              \"value\": \"<p><span class=\\\"panel-span-medium\\\">All internal network devices including, but not limited to, routers, firewalls, and access control servers, shall have unique passwords or other access control mechanisms that are aligned with the Group standards</span></p>\"\n				            },\n				            {\n				              \"type\": \"radio\",\n				              \"paramname\": \"NMS93Radio\",\n				              \"options\": [\n				                \"Yes\",\n				                \"No\",\n				                \"N/A\"\n				              ]\n				            },\n				            {\n				              \"type\": \"textarea\",\n				              \"paramname\": \"NMS93Comments\",\n				              \"rows\": \"2\"\n				            }\n				          ],\n				          [\n				            {\n				              \"type\": \"text\",\n				              \"value\": \"9.4\"\n				            },\n				            {\n				              \"type\": \"textandcomments\",\n				              \"id\": \"NMS94\",\n				              \"value\": \"<p><span class=\\\"panel-span-medium\\\">Any device that is to be connected to the network shall be authorised by the IT management. Such connection shall be assessed for its network impact and compatibility</span></p>\"\n				            },\n				            {\n				              \"type\": \"radio\",\n				              \"paramname\": \"NMS94Radio\",\n				              \"options\": [\n				                \"Yes\",\n				                \"No\",\n				                \"N/A\"\n				              ]\n				            },\n				            {\n				              \"type\": \"textarea\",\n				              \"paramname\": \"NMS94Comments\",\n				              \"rows\": \"2\"\n				            }\n				          ],\n				          [\n				            {\n				              \"type\": \"text\",\n				              \"value\": \"9.5\"\n				            },\n				            {\n				              \"type\": \"textandcomments\",\n				              \"id\": \"NMS95\",\n				              \"value\": \"<p><span class=\\\"panel-span-medium\\\">External parties accessing network devices remotely shall support the use of a strong multi-factor authentication mechanism</span></p>\"\n				            },\n				            {\n				              \"type\": \"radio\",\n				              \"paramname\": \"NMS95Radio\",\n				              \"options\": [\n				                \"Yes\",\n				                \"No\",\n				                \"N/A\"\n				              ]\n				            },\n				            {\n				              \"type\": \"textarea\",\n				              \"paramname\": \"NMS95Comments\",\n				              \"rows\": \"2\"\n				            }\n				          ],\n				          [\n				            {\n				              \"type\": \"text\",\n				              \"value\": \"9.6\"\n				            },\n				            {\n				              \"type\": \"textandcomments\",\n				              \"id\": \"NMS96\",\n				              \"value\": \"<p><sp